import React, { useState } from 'react';
import { Sweet } from '../../types';
import { useAuth } from '../../context/AuthContext';
import { sweetService } from '../../services/sweetService';
import './SweetCard.css';

interface SweetCardProps {
  sweet: Sweet;
  onUpdate: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

const SweetCard: React.FC<SweetCardProps> = ({ sweet, onUpdate, onEdit, onDelete }) => {
  const { isAdmin } = useAuth();
  const [purchaseQuantity, setPurchaseQuantity] = useState(1);
  const [restockQuantity, setRestockQuantity] = useState(10);
  const [loading, setLoading] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  const handlePurchase = async () => {
    if (purchaseQuantity <= 0) return;
    
    setLoading(true);
    try {
      await sweetService.purchase(sweet.id, purchaseQuantity);
      onUpdate();
    } catch (error) {
      console.error('Purchase failed:', error);
      alert('Purchase failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRestock = async () => {
    if (restockQuantity <= 0) return;
    
    setLoading(true);
    try {
      await sweetService.restock(sweet.id, restockQuantity);
      onUpdate();
    } catch (error) {
      console.error('Restock failed:', error);
      alert('Restock failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    setLoading(true);
    try {
      await sweetService.delete(sweet.id);
      onUpdate();
    } catch (error) {
      console.error('Delete failed:', error);
      alert('Delete failed. Please try again.');
    } finally {
      setLoading(false);
      setShowDeleteConfirm(false);
    }
  };

  const formatPrice = (price: any): string => {
    if (typeof price === 'string') {
      return parseFloat(price).toFixed(2);
    } else if (typeof price === 'number') {
      return price.toFixed(2);
    }
    return '0.00';
  };

  const getStockColor = (quantity: number) => {
    if (quantity > 10) return '#2ecc71';
    if (quantity > 0) return '#f39c12';
    return '#e74c3c';
  };

  const getSweetEmoji = (name: string) => {
    const emojiMap: Record<string, string> = {
      'Gulab Jamun': '🍬',
      'Rasgulla': '🍡',
      'Jalebi': '🥨',
      'Barfi': '🍰',
      'Ladoo': '🎪',
      'Malai Peda': '🍮'
    };
    return emojiMap[name] || '🍭';
  };

  return (
    <div className="sweet-card">
      <div className="sweet-image">
        {sweet.image_url ? (
          <img src={sweet.image_url} alt={sweet.name} />
        ) : (
          <div className="sweet-emoji">{getSweetEmoji(sweet.name)}</div>
        )}
      </div>
      
      <div className="sweet-content">
        <div className="sweet-header">
          <h3 className="sweet-name">{sweet.name}</h3>
          {isAdmin && onEdit && (
            <div className="sweet-actions">
              <button className="edit-btn" onClick={onEdit}>✏️</button>
              <button className="delete-btn" onClick={() => setShowDeleteConfirm(true)}>🗑️</button>
            </div>
          )}
        </div>
        
        <p className="sweet-description">{sweet.description}</p>
        
        <div className="sweet-details">
          <div className="detail-item">
            <span className="detail-label">Category:</span>
            <span className="detail-value">{sweet.category}</span>
          </div>
          <div className="detail-item">
            <span className="detail-label">Price:</span>
            <span className="detail-price">${formatPrice(sweet.price)}</span>
          </div>
          <div className="detail-item">
            <span className="detail-label">In Stock:</span>
            <span className="detail-stock" style={{ color: getStockColor(sweet.quantity) }}>
              {sweet.quantity}
            </span>
          </div>
        </div>
        
        <div className="action-section">
          {sweet.quantity > 0 ? (
            <div className="purchase-section">
              <input
                type="number"
                min="1"
                max={sweet.quantity}
                value={purchaseQuantity}
                onChange={(e) => setPurchaseQuantity(parseInt(e.target.value) || 1)}
                className="quantity-input"
              />
              <button 
                className="purchase-btn"
                onClick={handlePurchase} 
                disabled={loading || purchaseQuantity > sweet.quantity}
              >
                {loading ? 'Processing...' : 'Purchase'}
              </button>
            </div>
          ) : (
            <div className="out-of-stock">
              Out of Stock
            </div>
          )}
        </div>
        
        {isAdmin && (
          <div className="admin-section">
            <h4 className="admin-title">Admin Actions</h4>
            <div className="restock-section">
              <input
                type="number"
                min="1"
                value={restockQuantity}
                onChange={(e) => setRestockQuantity(parseInt(e.target.value) || 10)}
                className="quantity-input"
              />
              <button 
                className="restock-btn"
                onClick={handleRestock} 
                disabled={loading}
              >
                {loading ? 'Processing...' : 'Restock'}
              </button>
            </div>
          </div>
        )}

        {showDeleteConfirm && (
          <div className="delete-confirm-modal">
            <div className="delete-confirm-content">
              <h3>Confirm Delete</h3>
              <p>Are you sure you want to delete "{sweet.name}"? This action cannot be undone.</p>
              <div className="delete-confirm-buttons">
                <button 
                  className="confirm-delete-btn"
                  onClick={handleDelete}
                  disabled={loading}
                >
                  {loading ? 'Deleting...' : 'Yes, Delete'}
                </button>
                <button 
                  className="cancel-delete-btn"
                  onClick={() => setShowDeleteConfirm(false)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SweetCard;
